import { useEffect, useState } from 'react';
import { LivingAppsService } from '@/services/livingAppsService';
import type { Kurse, Anmeldungen } from '@/types/app';
import { BookOpen, Users, GraduationCap, DoorOpen, ClipboardList, TrendingUp, CheckCircle, Clock, XCircle } from 'lucide-react';
import { format, parseISO, isAfter, isBefore, isToday } from 'date-fns';
import { de } from 'date-fns/locale';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, Cell } from 'recharts';

interface Stats {
  kurse: number;
  aktiveKurse: number;
  dozenten: number;
  teilnehmer: number;
  raeume: number;
  anmeldungen: number;
  bezahltQuote: number;
  umsatz: number;
}

const COLORS = ['#4f7cff', '#6d97ff', '#8db0ff', '#adc9ff'];

export default function DashboardOverview() {
  const [stats, setStats] = useState<Stats>({
    kurse: 0, aktiveKurse: 0, dozenten: 0, teilnehmer: 0,
    raeume: 0, anmeldungen: 0, bezahltQuote: 0, umsatz: 0,
  });
  const [recentKurse, setRecentKurse] = useState<Kurse[]>([]);
  const [statusData, setStatusData] = useState<{ name: string; value: number }[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function loadAll() {
      try {
        const [dozentenData, teilnehmerData, raeumeData, kurseData, anmeldungenData] = await Promise.all([
          LivingAppsService.getDozenten(),
          LivingAppsService.getTeilnehmer(),
          LivingAppsService.getRaeume(),
          LivingAppsService.getKurse(),
          LivingAppsService.getAnmeldungen(),
        ]);

        const aktiveKurse = kurseData.filter(k => k.fields.status === 'aktiv').length;
        const bezahlt = anmeldungenData.filter(a => a.fields.bezahlt).length;
        const bezahltQuote = anmeldungenData.length > 0 ? Math.round((bezahlt / anmeldungenData.length) * 100) : 0;

        // Umsatz aus bezahlten Anmeldungen * Kurspreis
        let umsatz = 0;
        const bezahltAnmeldungen = anmeldungenData.filter(a => a.fields.bezahlt);
        for (const anm of bezahltAnmeldungen) {
          if (anm.fields.kurs) {
            const kursId = anm.fields.kurs.match(/([a-f0-9]{24})$/i)?.[1];
            const kurs = kurseData.find(k => k.record_id === kursId);
            if (kurs?.fields.preis) umsatz += kurs.fields.preis;
          }
        }

        const statusCounts = { geplant: 0, aktiv: 0, abgeschlossen: 0, abgesagt: 0 };
        kurseData.forEach(k => {
          const s = k.fields.status || 'geplant';
          statusCounts[s as keyof typeof statusCounts]++;
        });
        setStatusData([
          { name: 'Geplant', value: statusCounts.geplant },
          { name: 'Aktiv', value: statusCounts.aktiv },
          { name: 'Abgeschlossen', value: statusCounts.abgeschlossen },
          { name: 'Abgesagt', value: statusCounts.abgesagt },
        ]);

        const sorted = [...kurseData].sort((a, b) => {
          const da = a.fields.startdatum || '';
          const db = b.fields.startdatum || '';
          return db.localeCompare(da);
        }).slice(0, 5);
        setRecentKurse(sorted);

        setStats({
          kurse: kurseData.length,
          aktiveKurse,
          dozenten: dozentenData.length,
          teilnehmer: teilnehmerData.length,
          raeume: raeumeData.length,
          anmeldungen: anmeldungenData.length,
          bezahltQuote,
          umsatz,
        });
      } catch (e) {
        console.error('Failed to load stats:', e);
      } finally {
        setLoading(false);
      }
    }
    loadAll();
  }, []);

  const statusIcon = (status?: string) => {
    if (status === 'aktiv') return <CheckCircle size={14} className="text-emerald-500" />;
    if (status === 'abgesagt') return <XCircle size={14} className="text-rose-500" />;
    if (status === 'abgeschlossen') return <CheckCircle size={14} className="text-blue-400" />;
    return <Clock size={14} className="text-amber-500" />;
  };

  const statusLabel = (status?: string) => {
    const map: Record<string, string> = { geplant: 'Geplant', aktiv: 'Aktiv', abgeschlossen: 'Abgeschlossen', abgesagt: 'Abgesagt' };
    return map[status || 'geplant'] || 'Geplant';
  };

  const statusBg = (status?: string) => {
    if (status === 'aktiv') return 'bg-emerald-50 text-emerald-700 border border-emerald-200';
    if (status === 'abgesagt') return 'bg-rose-50 text-rose-700 border border-rose-200';
    if (status === 'abgeschlossen') return 'bg-blue-50 text-blue-700 border border-blue-200';
    return 'bg-amber-50 text-amber-700 border border-amber-200';
  };

  return (
    <div className="space-y-8">
      {/* Hero Section */}
      <div className="relative overflow-hidden rounded-2xl p-8 shadow-hero" style={{ background: 'var(--gradient-hero)' }}>
        <div className="relative z-10">
          <p className="text-xs font-semibold uppercase tracking-widest mb-2" style={{ color: 'oklch(0.7 0.1 255)' }}>
            Willkommen zur√ºck
          </p>
          <h1 className="text-4xl font-bold tracking-tight text-white mb-1">
            KursVerwaltung
          </h1>
          <p className="text-base font-light" style={{ color: 'oklch(0.75 0.05 255)' }}>
            Ihre zentrale √úbersicht f√ºr alle Kurse, Dozenten und Teilnehmer.
          </p>
          <div className="mt-6 flex gap-6">
            <div>
              <p className="text-3xl font-bold text-white">{loading ? '‚Äì' : stats.kurse}</p>
              <p className="text-xs" style={{ color: 'oklch(0.7 0.08 255)' }}>Kurse gesamt</p>
            </div>
            <div className="w-px" style={{ background: 'oklch(1 0 0 / 0.15)' }} />
            <div>
              <p className="text-3xl font-bold text-white">{loading ? '‚Äì' : stats.aktiveKurse}</p>
              <p className="text-xs" style={{ color: 'oklch(0.7 0.08 255)' }}>Aktuell aktiv</p>
            </div>
            <div className="w-px" style={{ background: 'oklch(1 0 0 / 0.15)' }} />
            <div>
              <p className="text-3xl font-bold text-white">{loading ? '‚Äì' : `${stats.umsatz.toLocaleString('de-DE')} ‚Ç¨`}</p>
              <p className="text-xs" style={{ color: 'oklch(0.7 0.08 255)' }}>Bezahlter Umsatz</p>
            </div>
          </div>
        </div>
        {/* Decorative circles */}
        <div className="absolute top-0 right-0 w-64 h-64 rounded-full opacity-10" style={{ background: 'oklch(0.6 0.18 250)', transform: 'translate(30%, -30%)' }} />
        <div className="absolute bottom-0 right-20 w-40 h-40 rounded-full opacity-10" style={{ background: 'oklch(0.5 0.2 260)', transform: 'translateY(40%)' }} />
      </div>

      {/* KPI Cards */}
      <div className="grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-5 gap-4">
        {[
          { label: 'Dozenten', value: stats.dozenten, icon: GraduationCap, color: 'oklch(0.58 0.19 250)' },
          { label: 'Teilnehmer', value: stats.teilnehmer, icon: Users, color: 'oklch(0.58 0.19 250)' },
          { label: 'R√§ume', value: stats.raeume, icon: DoorOpen, color: 'oklch(0.58 0.19 250)' },
          { label: 'Kurse', value: stats.kurse, icon: BookOpen, color: 'oklch(0.58 0.19 250)' },
          { label: 'Anmeldungen', value: stats.anmeldungen, icon: ClipboardList, color: 'oklch(0.58 0.19 250)' },
        ].map(({ label, value, icon: Icon, color }) => (
          <div key={label} className="rounded-xl p-5 border shadow-card" style={{ background: 'var(--card)', boxShadow: 'var(--shadow-card)' }}>
            <div className="flex items-center justify-between mb-3">
              <p className="text-xs font-semibold uppercase tracking-wider text-muted-foreground">{label}</p>
              <div className="w-8 h-8 rounded-lg flex items-center justify-center" style={{ background: `${color}22` }}>
                <Icon size={15} style={{ color }} />
              </div>
            </div>
            <p className="text-3xl font-bold tracking-tight">{loading ? '‚Äì' : value}</p>
          </div>
        ))}
      </div>

      {/* Charts & Recent Courses */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Kursstatus Chart */}
        <div className="rounded-xl border p-6 shadow-card" style={{ background: 'var(--card)' }}>
          <h3 className="text-sm font-semibold uppercase tracking-wider text-muted-foreground mb-1">Kursstatus</h3>
          <p className="text-2xl font-bold mb-4">{loading ? '‚Äì' : stats.kurse} Kurse</p>
          <ResponsiveContainer width="100%" height={180}>
            <BarChart data={statusData} barSize={28}>
              <XAxis dataKey="name" tick={{ fontSize: 11, fill: 'oklch(0.52 0.03 255)' }} axisLine={false} tickLine={false} />
              <YAxis hide />
              <Tooltip
                contentStyle={{ borderRadius: '8px', border: '1px solid oklch(0.88 0.01 255)', fontSize: 12 }}
                cursor={{ fill: 'oklch(0.93 0.03 255)' }}
              />
              <Bar dataKey="value" radius={[6, 6, 0, 0]}>
                {statusData.map((_, i) => <Cell key={i} fill={COLORS[i % COLORS.length]} />)}
              </Bar>
            </BarChart>
          </ResponsiveContainer>
        </div>

        {/* Bezahlt-Quote */}
        <div className="rounded-xl border p-6 shadow-card flex flex-col justify-between" style={{ background: 'var(--card)' }}>
          <div>
            <h3 className="text-sm font-semibold uppercase tracking-wider text-muted-foreground mb-1">Zahlungsquote</h3>
            <p className="text-5xl font-bold tracking-tight mb-1">{loading ? '‚Äì' : `${stats.bezahltQuote}%`}</p>
            <p className="text-sm text-muted-foreground">der Anmeldungen bezahlt</p>
          </div>
          <div className="mt-6">
            <div className="flex justify-between text-xs text-muted-foreground mb-1">
              <span>Bezahlt</span>
              <span>{stats.bezahltQuote}%</span>
            </div>
            <div className="h-2.5 rounded-full overflow-hidden" style={{ background: 'oklch(0.93 0.03 255)' }}>
              <div
                className="h-full rounded-full transition-all duration-700"
                style={{ width: `${stats.bezahltQuote}%`, background: 'oklch(0.58 0.19 250)' }}
              />
            </div>
            <div className="mt-4 flex items-center gap-2 text-sm">
              <TrendingUp size={15} style={{ color: 'oklch(0.58 0.19 250)' }} />
              <span className="text-muted-foreground">{loading ? '‚Äì' : `${stats.umsatz.toLocaleString('de-DE')} ‚Ç¨ Umsatz`}</span>
            </div>
          </div>
        </div>

        {/* Recent Courses */}
        <div className="rounded-xl border p-6 shadow-card" style={{ background: 'var(--card)' }}>
          <h3 className="text-sm font-semibold uppercase tracking-wider text-muted-foreground mb-4">Aktuelle Kurse</h3>
          {loading ? (
            <div className="space-y-3">
              {[1,2,3].map(i => <div key={i} className="h-12 rounded-lg animate-pulse" style={{ background: 'oklch(0.93 0.03 255)' }} />)}
            </div>
          ) : recentKurse.length === 0 ? (
            <div className="flex flex-col items-center justify-center h-40 text-muted-foreground text-sm">
              <BookOpen size={32} className="mb-2 opacity-30" />
              <p>Noch keine Kurse</p>
            </div>
          ) : (
            <div className="space-y-2.5">
              {recentKurse.map(kurs => (
                <div key={kurs.record_id} className="flex items-center justify-between p-3 rounded-lg" style={{ background: 'oklch(0.975 0.005 255)' }}>
                  <div className="min-w-0">
                    <p className="text-sm font-medium truncate">{kurs.fields.titel || '‚Äì'}</p>
                    <p className="text-xs text-muted-foreground">
                      {kurs.fields.startdatum
                        ? format(parseISO(kurs.fields.startdatum), 'dd. MMM yyyy', { locale: de })
                        : '‚Äì'}
                    </p>
                  </div>
                  <span className={`ml-2 shrink-0 inline-flex items-center gap-1 rounded-md px-2 py-0.5 text-xs font-medium ${statusBg(kurs.fields.status)}`}>
                    {statusIcon(kurs.fields.status)}
                    {statusLabel(kurs.fields.status)}
                  </span>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

